<?php  if ( ! defined('BASEPATH')) exit('No direct script access allowed');////tgl_indo helper created by justudin//with some modified filter//from unix time on oracle database//if ( ! function_exists('tgl_indo')){	function tgl_indo($tgl)	{		$tgl1=date('Y-m-d',$tgl);		$jam=date('H:i',$tgl);		$ubah = gmdate($tgl1, time()+60*60*8);		$pecah = explode("-",$ubah);		$tanggal = $pecah[2];		$bulan = bulan($pecah[1]);		$tahun = $pecah[0];		return $tanggal.' '.$bulan.' '.$tahun.' '.$jam.' WIB';	}	function tgl_indo_short($tgl)	{		$tgl1=date('Y-m-d',$tgl);		$jam=date('H:i',$tgl);		$ubah = gmdate($tgl1, time()+60*60*8);		$pecah = explode("-",$ubah);		$tanggal = $pecah[2];		$bulan = bulan($pecah[1]);		$tahun = $pecah[0];		return $tanggal.' '.$bulan.' '.$tahun;	}}if ( ! function_exists('bulan')){	function bulan($bln)	{		switch ($bln)		{			case 1:				return "Januari";				break;			case 2:				return "Februari";				break;			case 3:				return "Maret";				break;			case 4:				return "April";				break;			case 5:				return "Mei";				break;			case 6:				return "Juni";				break;			case 7:				return "Juli";				break;			case 8:				return "Agustus";				break;			case 9:				return "September";				break;			case 10:				return "Oktober";				break;			case 11:				return "November";				break;			case 12:				return "Desember";				break;		}	}}if ( ! function_exists('nama_hari')){	function nama_hari($tanggal)	{		$tanggal1=date('Y-m-d',$tanggal);		$ubah = gmdate($tanggal1, time()+60*60*8);		$pecah = explode("-",$ubah);		$tgl = $pecah[2];		$bln = $pecah[1];		$thn = $pecah[0];		$nama = date("l", mktime(0,0,0,$bln,$tgl,$thn));		$nama_hari = "";		if($nama=="Sunday") {$nama_hari="Ahad";}		else if($nama=="Monday") {$nama_hari="Senin";}		else if($nama=="Tuesday") {$nama_hari="Selasa";}		else if($nama=="Wednesday") {$nama_hari="Rabu";}		else if($nama=="Thursday") {$nama_hari="Kamis";}		else if($nama=="Friday") {$nama_hari="Jum'at";}		else if($nama=="Saturday") {$nama_hari="Sabtu";}		return $nama_hari;	}}/*End of tgl_indo helper*/